import { useState } from 'react';
import { Card, Form, Button } from 'react-bootstrap';

function UpdateField() {
    const [message, setMessage] = useState(null);
    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            if (message) {
                const response = await fetch(`http://127.0.0.1:3010/api/createpost/`, {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                    Authorization: `Bearer ${sessionStorage.getItem('token')}`,
                },
                body: JSON.stringify(message),
                });
                if (!response.ok) {
                    throw new Error("Network response was not ok");
                }
            }
            else {
                // Реализация всплывающего окна (Пустое сообщение)
            }
        } catch (error) {
          console.error(error);
        }
      };
    return (
        <Card className="my-2" bg="light" style={{ borderRadius: '25px', borderColor: 'none' }}>
            <Card.Body style={{ display: 'flex', flexDirection: 'column' }}>
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-end', marginBottom: '10px' }}>
                    <Form.Control onSubmit={handleSubmit} as="textarea" type="text" placeholder="Enter your post" />
                    <Button variant="primary" type="submit">Submit</Button>
                </div>
            </Card.Body>
        </Card>
    );
}

export default UpdateField;
