import React from 'react';
import { Card, Button, Image } from 'react-bootstrap';

function Post(props) {
  const { user, content } = props;
  return (
    <Card className="my-2" bg="light" style={{ borderRadius: '25px', borderColor: 'none' }}>
  <Card.Body style={{ display: 'flex', flexDirection: 'column' }}>
    <Card.Text style={{ flexGrow: '1' }}>{content}</Card.Text>
    <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
      <div style={{ display: 'flex', alignItems: 'center' }}>
        <Button variant="link">
          <Image src="./edit.svg" width="20" height="20" alt="Edit" />
        </Button>
        <Button variant="link">
          <Image src="./trash.svg" width="20" height="20" alt="Delete" />
        </Button>
      </div>
      <div style={{ display: 'flex', justifyContent: "flex-end", alignItems: "center" }}>
        <Card.Text style={{ fontStyle: 'italic', margin: '0', marginLeft: '10px' }}>&#8210;{user}</Card.Text>
        <div style={{ fontStyle: 'italic', fontSize: '0.8rem' }}>{new Date().toLocaleString()}</div>
      </div>
    </div>
  </Card.Body>
</Card>

  );
}

export default Post;
