        <div className="Auth">
            <div className="d-flex justify-content-center align-items-center vh-100">
                <Form onSubmit={handleSubmit} style={{ maxWidth: '500px', width: '100%' }}>
                <h2 className="text-center mb-4">Регистрация</h2>
                <Form.Group className="row" controlId="formBasicName">
                    <Form.Label className="col-form-label col-sm-3">ФИО:</Form.Label>
                    <div className="col-sm-9">
                    <Form.Control type="text" placeholder="Введите имя" value={name} onChange={(e) => setName(e.target.value)}/>
                    </div>
                </Form.Group>

                <Form.Group className="row mt-3" controlId="formBasicEmail">
                    <Form.Label className="col-form-label col-sm-3">Email:</Form.Label>
                    <div className="col-sm-9">
                    <Form.Control type="email" placeholder="Введите email" value={email} onChange={(e) => setEmail(e.target.value)}/>
                    </div>
                </Form.Group>

                <Form.Group className="row mt-3" controlId="formBasicPassword">
                    <Form.Label className="col-form-label col-sm-3">Пароль:</Form.Label>
                    <div className="col-sm-9">
                    <Form.Control type="password" placeholder="Введите пароль" value={password} onChange={(e) => setPassword(e.target.value)}/>
                    </div>
                </Form.Group>

                <Form.Group className="d-flex justify-content-end mt-3">
                    <Button variant="primary" type="submit">
                    Зарегистрироваться
                    </Button>
                </Form.Group>
                </Form>
            </div>
        </div>


    );
}


export default Auth;
