import React from 'react';
import { Card } from 'react-bootstrap';

function Post(props) {
  const { user, content } = props;
  return (
    <Card className="my-2" bg="light" border="secondary" style={{ borderRadius: '25px', borderColor: 'unset' }}>
        <Card.Body style={{ display: 'flex', flexDirection: 'column' }}>
            <Card.Text style={{ flexGrow: '1' }}>{content}</Card.Text>
            <div style={{ display: 'flex', justifyContent: 'flex-end', alignItems: 'center' }}>
                <Card.Text style={{ fontStyle: 'italic', margin: '0', marginRight: '10px' }}>&#8210;{user},</Card.Text>
                <div style={{ fontStyle: 'italic', fontSize: '0.8rem'}}>{new Date().toLocaleString()}</div>
            </div>
        </Card.Body>
</Card>

  );
}

export default Post;
