import React from 'react';
import Card from 'react-bootstrap/Card';

function Post(props) {
  const { user, content } = props;
  return (
    <Card className="my-2" bg="light" border="secondary" style={{ borderRadius: '12px' }}>
  <Card.Body style={{ display: 'flex', flexDirection: 'column' }}>
    <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-end' }}>
      <Card.Title style={{ fontStyle: 'italic' }}>{user}</Card.Title>
      <div style={{ fontStyle: 'italic', fontSize: '0.8rem', alignSelf: 'flex-end' }}>{new Date().toLocaleString()}</div>
    </div>
    <Card.Text style={{ flexGrow: '1' }}>{content}</Card.Text>
  </Card.Body>
</Card>

  );
}

export default Post;
