import React, { useState, useEffect } from 'react';
import Post from './Post';
import { Pagination } from 'react-bootstrap';
import UpdateField from './Field';

function Feed() {
  const [currentPage, setCurrentPage] = useState(1);
  const [posts, setPosts] = useState([]);
  const [postAmount, setPostAmount] = useState(0);
  const postsPerPage = 20;
  console.log(posts);
  const indexOfLastPost = currentPage * postsPerPage;
  const indexOfFirstPost = indexOfLastPost - postsPerPage;
  useEffect((indexOfLastPost, indexOfFirstPost) => {
    fetch(`http://127.0.0.1:3010/api/countposts`)
    .then(response => response.json())
    .then(data => setPostAmount(data.count))
    .catch(error => console.error(error.message));
    fetch(`http://127.0.0.1:3010/api/posts?limit=${indexOfLastPost}&offset=${indexOfFirstPost}`)
      .then(response => response.json())
      .then(data => setPosts(data.posts))
      .catch(error => console.error(error.message));
  }, [currentPage]);
  const currentPosts = posts;
  // Change page
  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  // Build pagination items
  const pageItems = [];
  for (let i = 1; i <= Math.ceil(postAmount / postsPerPage); i++) {
    pageItems.push(
      <Pagination.Item key={i} active={i === currentPage} onClick={() => paginate(i)}>
        {i}
      </Pagination.Item>
    );
  }
  return (
    <div>
        <div style={{ margin: '0 auto', width: '50%' }}>
            <UpdateField/>
            {currentPosts.map((post, index) => (
                <Post key={index} post_id={post.post_id} username={post.name} message={post.message} timestamp={post.timestamp}/>
            ))}
        <Pagination>{pageItems}</Pagination>
        </div>
    </div>
  );
}

export default Feed;
